import React, { Component } from "react";
import { movies } from "./getMovies";
import "./movies.css";

export default class Movies extends Component {
  constructor(props) {
    super(props);
    this.state = {
      hover: " ",
    };
  }
  render() {
    let movie = movies.results;
    return (
      <>
        <div>
          <h3 className="h1_movies text-center">
            <strong>Trending</strong>
          </h3>
          <div className="movies-list">
            {movie.map((movieObj) => (
              <div
                className="card movies-card"
                onMouseEnter={() => this.setState({ hover: movieObj.id })}
                onMouseLeave={() => this.setState({ hover: " " })}
              >
                <img
                  src={`
            https://www.themoviedb.org/t/p/w600_and_h900_bestv2${movieObj.backdrop_path}`}
                  className="card-img-top movies-img"
                  alt={movieObj.title}
                />
                {/* <div className="card-body"> */}
                <h3 className="card-title movies-title">
                  {movieObj.original_title}
                </h3>
                {/* <p className="card-text movies-text">{movieObj.overview}</p> */}
                <div className="button-wrapper movies-button">
                  {this.state.hover == movieObj.id && (
                    <a href="#" className="btn btn-primary">
                      Nominate
                    </a>
                  )}
                </div>
              </div>
            ))}
          </div>
          <div className="movies-pagination">
            <nav aria-label="Page navigation example">
              <ul class="pagination">
                <li class="page-item">
                  <a class="page-link" href="#">
                    Previous
                  </a>
                </li>
                <li class="page-item">
                  <a class="page-link" href="#">
                    1
                  </a>
                </li>
                <li class="page-item">
                  <a class="page-link" href="#">
                    2
                  </a>
                </li>
                <li class="page-item">
                  <a class="page-link" href="#">
                    3
                  </a>
                </li>
                <li class="page-item">
                  <a class="page-link" href="#">
                    Next
                  </a>
                </li>
              </ul>
            </nav>
          </div>
        </div>
      </>
    );
  }
}
