import React, { Component } from "react";
import { movies } from "./getMovies";
import "./movies.css";

export default class Movies extends Component {
  render() {
    let movie = movies.results;
    return (
      <>
        <div>
          <h3 className="h1_movies text-center">
            <strong>Trending</strong>
          </h3>
          <div>
            {movie.map((movieObj) => (
              <div className="card movies-card">
                <img
                  src={`
            https://www.themoviedb.org/t/p/w600_and_h900_bestv2${movieObj.backdrop_path}`}
                  className="card-img-top movies-img"
                  alt={movieObj.title}
                />
                {/* <div className="card-body"> */}
                <h1 className="card-title movies-title">
                  {movieObj.original_title}
                </h1>
                <p className="card-text movies-text">{movieObj.overview}</p>
                <a href="#" className="btn btn-primary">
                  Go somewhere
                </a>
              </div>
            ))}
          </div>
        </div>
      </>
    );
  }
}
